import React, { Component } from "react";
import FruitCard from "./components/FruitCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import fruits from "./fruits.json";

class App extends Component {
  // Setting this.state.fruits to the fruits json array
  state = {
    fruits: fruits
  };

  removeFruits = id => {
    // Filter this.state.fruits for fruits with an id not equal to the id being removed
    const fruits = this.state.fruits.filter(fruit => fruit.id !== id);
    // Set this.state.fruits equal to the new fruits array
    this.setState({ fruits: fruits });
  };

  // Map over this.state.fruits and render a fruitCard component for each fruit object
  render() {
    return (
      <Wrapper>
        <Title>Fruits List</Title>
        {this.state.fruits.map(fruit => (
          <FruitCard
            removeFruit={this.removeFruit}
            id={fruit.id}
            key={fruit.id}
            name={fruit.name}
            image={fruit.image}
            occupation={fruit.occupation}
            location={fruit.location}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
